m => {

// On fixe l'attribut id
m.Value.SetAttributeValue("id", m.Value.XPathSelectElement("field[@id='id']")?.Value);
XElement element = new XElement("recordtype");

string code = "jugement";
XAttribute attribute = new XAttribute("code", code);
element.Add(attribute);
element.Value = "jugement";

m.Value.Add(element);
}
